import React from "react";
import './App.css';


class App extends React.Component {
  constructor(){
    super();

    this.state={
      monsters:[],
      searchField:'',
      language:'',
      locale: '',
    };
    /* this.handleChange = this.handleChange.bind(this); instead of this line we can use arrow function */
  }

  componentDidMount(){
    const languageStoredInLocalStorage = localStorage.getItem('language') === '';
    const language = languageStoredInLocalStorage ? localStorage.getItem('language') : 'ba_en';
    this.setState({ language, languageStoredInLocalStorage });

    fetch('https://jsonplaceholder.typicode.com/users')
    .then(response =>response.json())
    .then(users => this.setState({ monsters:users }));
  }

  handleChange = (e) => {
    this.setState({ searchField: e.target.value});
  }


  render() {
    const { monsters, searchField,language } = this.state;
    const locale = i18n.language;
    const filteredMonsters = monsters.filter(monsters => monsters.name.toLowerCase().includes(searchField.toLowerCase())
    );

    return (
      <div className="App">
          {monsters}
      </div>
    );
  }
}

export default App;
