import React from "react";
import './App.css';


class App extends React.Component {
  constructor(){
    super();

    this.state={
      customers:[],
    };
    /* this.handleChange = this.handleChange.bind(this); instead of this line we can use arrow function */
  }

  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/users')
    .then(response =>response.json())
    .then(users => this.setState({ customers:users }));
  }


  render() {
    const { customers } = this.state;
    // const filteredMonsters = monsters.filter(monsters => monsters.name.toLowerCase().includes(searchField.toLowerCase()));
    return (
      <div className="App">
      {
        this.state.customers.map((customers) => {
          return(
            <p key={customers.id}>{customers.name}</p> 
          )
        }
        )
      }

      </div>
    );
  }
}

export default App;
